query getTracks($ids: [ID!]!) {
  getTracks(ids: $ids) {
    id
    title
    searchTitle
    position
    duration
    availability
    artistTemplate
    condition
    explicit
    lyrics
    zchan
    hasFlac
    artists {
      id
      title
    }
    release {
      id
      title
      image {
        src
        palette
        paletteBottom
      }
    }
  }
}
